mapper.select:
select

from pms_inf


    and product_name like '%' #{name} '%'


    and product_type_id=#{type}


    and product_color like '%' #{color} '%'


    and product_material like '%' #{material} '%'


    and product_del !=#{del}


limit #{pageNo},#{pageSize}

select count(product_id) from pms_inf


    and product_name like '%' #{name} '%'


    and product_type_id=#{type}


    and product_color like '%' #{color} '%'


    and product_material like '%' #{material} '%'


    and product_del !=#{del}

select product_contour
from pms_inf
where product_id = #{productId,jdbcType=BIGINT}
